import pandas as pdimport numpy as npdf=pd.read_csv("medallero_Panamericanos_Lima2019.csv")data=pd.DataFrame(df)print(data)meOro=data['Oro'].mean()moOro=data['Oro'].mode()mnaOro=data['Oro'].median()print("Media de Oro :",meOro)print("Moda de Oro : ",moOro)print("Mediana de Oro :",mnaOro)mePlata=data['Plata'].mean()moPlata=data['Plata'].mode()mnaPlata=data['Plata'].median()print("Media de Plata :",mePlata)print("Moda de Plata : ",moPlata)print("Mediana de Plata :",mnaPlata)